package patterns;
import java.util.*;
public class Square_Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n = 7;
        for(int i=0;i<7;i++) {
        	for(int j=0;j<7;j++) {
        		System.out.print(n);
        	}
        	
        	
        	System.out.println();
        }
        	
	}

}
